import time
import collections

class Solution(object):

    def minWindow(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        
        if s is None or len(s) == 0:
            return ""
        
        cur_best = ""
        needs = collections.Counter(t)  # need target char with counter
        need_len = len(t)               # need length
        
        tail = 0
        for head,c in enumerate(s):
            if needs[c] > 0:
                need_len -= 1

            needs[c] -= 1

            # find current minimun substr
            while tail <= head and need_len == 0:
                if cur_best == "" or head - tail + 1 < len(cur_best):
                    cur_best = s[tail:head+1]
                needs[s[tail]] += 1
                if needs[s[tail]] > 0:
                    need_len += 1
                tail += 1
        
        return cur_best
        
def main():
    # Case1: s==t
    # Case2: s contains t in head
    # Case3: s contains t in last
    # Case4: s does not contains t
    # Case5: s contains t while t has duplicate chars
    s_list = ["AF","AF","AFG","A","AAE"]
    t_list = ["AF","A","FG","ABC","AA"]
    expect_lsit = ["AF","A","FG","","AA"]
    for s, t, e in zip(s_list,t_list, expect_lsit):
        start = time.time()
        ret = Solution().minWindow(s, t)
        end = time.time()
        print(ret, (end-start)*1000, ret == e)
    s = ""
    t = ""
    start = time.time()
    ret = Solution().minWindow(s, t)
    end = time.time()
    print(ret, (end-start)*1000) 

if __name__ == '__main__':
    main()     